    function onNextendSliderGeneratorLayouts($type, &$slide) {
        switch ($type) {
            case 'image_quick':
                self::image_quick($slide);
                break;
            case 'image_extended':
                self::image_extended($slide);
                break;
            case 'image':
                self::image($slide);
                break;
            case 'article':
                self::image_extended($slide);
                break;
            case 'social_post':
                self::social_post($slide);
                break;
            case 'youtube':
                self::youtube($slide);
                break;
            case 'product':
                self::product($slide);
                break;
        }
    }

    static function image(&$slide) {
        self::image_default($slide);
        self::image_extended_caption($slide);
    }

    static function image_extended(&$slide) {
        self::image_default($slide);
        self::image_extended_caption($slide);
        self::image_extended_caption_description($slide);
        self::image_extended_center($slide);
        if (smartsliderIsFull()) self::image_extended_center_author($slide);
        if (smartsliderIsFull()) self::image_extended_leftimage_right_with_description_center($slide);
        if (smartsliderIsFull()) self::image_extended_leftimage_right_with_description_left($slide);
    }

    static function image_quick(&$slide) {
        self::image_default($slide);
        self::image_extended_caption($slide);
        self::image_extended_caption_description($slide);
        self::image_extended_center($slide);
        if (smartsliderIsFull()) self::image_extended_center_author($slide);
        if (smartsliderIsFull()) self::image_extended_leftimage_right_with_description_center($slide);
        if (smartsliderIsFull()) self::image_extended_leftimage_right_with_description_left($slide);
    }

    static function social_post(&$slide) {
        self::social_post_default($slide);
    }

    static function product(&$slide) {
        self::product_default($slide);
        self::product_2($slide);
        self::product_3($slide);
        self::product_4($slide);
    }

